import "./App.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
} from "react-router-dom";
import { useState, useEffect, useContext } from "react";

import Navbar from "./components/Navbar";
import Home from "./components/Home";
import globalContext from "./context/globalContext";

const App = () => {
  // const [isAuthenticated, setIsAuthenticated] = useState(false);
  const {
    store: { isAuthenticated }
  } = useContext(globalContext)
  console.log(isAuthenticated)
  return (
    <div className="App">
      <Router>
        <header>
          <Navbar />
        </header>
        <div>
          <Switch>
            <Route
              path="/about"
              exact
              render={() => <About isAuthenticated={isAuthenticated} />}
            />
            <Route
              path="/about/company"
              exact
              render={() => <About isAuthenticated={isAuthenticated} />}
            />
            <Route
              path="/about/jobs"
              exact
              render={() => <About isAuthenticated={isAuthenticated} />}
            />
            <Route
              path="/about/jobs/edit"
              exact
              render={() => {
                return (
                  <EditJobs
                    isAuthenticated={isAuthenticated}
                    // setIsAuthenticated={setIsAuthenticated}
                  />
                );
              }}
            />
            <Route
              path="/signin"
              exact
              render={() => (
                <SignIn
                  isAuthenticated={isAuthenticated}
                  // setIsAuthenticated={setIsAuthenticated}
                />
              )}
            />
            <Route path="/" component={Home} />
          </Switch>
        </div>
      </Router>
    </div>
  );
};



const SignIn = ({ isAuthenticated, setIsAuthenticated }) => {
  const clickHandler = (event) => {
    setIsAuthenticated(event.target.checked);
  };
  return (
    <>
      <input
        name="authenticated"
        type="checkbox"
        className="checkbox"
        onClick={(e) => clickHandler(e)}
        value={isAuthenticated}
      />
      <label for="authenticated">I'm Authenticated</label>
    </>
  );
};

const About = ({ isAuthenticated }) => {
  return (
    <div className="d-flex flex-row">
      <SideBar isAuthenticated={isAuthenticated} />
      <div className="about">About us..</div>
    </div>
  );
};



const SideBar = () => {
  return (
    <div className="sidebar">
      <nav className="d-flex flex-col">
        <li>
          <Link to="/about/company">Company</Link>
        </li>
        <li>
          <Link to="/about/jobs">Jobs</Link>
        </li>
        <li>
          <Link to="/about/jobs/edit">Edit Jobs</Link>
        </li>
      </nav>
    </div>
  );
};

const EditJobs = ({ isAuthenticated, setIsAuthenticated }) => {
  useEffect(() => {
    if (!isAuthenticated) {
      <Redirect
        to={{
          pathname: "/signin",
          state: {
            isAuthenticated: isAuthenticated,
            setIsAuthenticated: setIsAuthenticated,
          },
        }}
      />;
    }
  }, [isAuthenticated]);
  return <About isAuthenticated={isAuthenticated} />;
};
export default App;
